import React, {Component} from 'react';


class Header extends Component {

    constructor(props) {
        super(props);
        this.state = {
            time: new Date().toTimeString()
        }
    }

    updateTimeState = () => this.setState({time: new Date().toTimeString()})

    componentDidMount() {
        setInterval( () => this.updateTimeState(), 1000);
    }

    render() {
        return <header>a header with timeString: {this.state.time}</header>
    }
}


export default Header;
